#ifdef GLOBAL
gl_FragColor=(texture2D(lm,gl_PointCoord)*gl_FragColor)-vec4(0.15,0.15,0.15,0.0);
#else
gl_FragColor=(texture2D(lm,gl_PointCoord)*gl_FragColor)+vec4(0.27,0.27,0.27,0.0);
#endif
gl_FragColor.a*=a;}else{gl_FragColor.a*=(0.3*a);}if(gl_FragColor.r<=1e-6&&gl_FragColor.g<=1e-6&&gl_FragColor.b<=1e-6){gl_FragColor.rgb=vec3(0.1,0.1,0.1);gl_FragColor.a=0.1;}}

