uniform sampler2D oo;uniform float mi;uniform vec3 ei;uniform vec4 es;uniform vec4 op;uniform vec4 eo;uniform vec3 ll;uniform bool drawPath;varying vec4 cc;varying vec3 hd;varying vec3 dh;void main(){float a=mi*0.01;gl_FragColor.rgb=cc.rgb;gl_FragColor.a=1.0;if(!drawPath){
#ifdef GLOBAL
gl_FragColor=(texture2D(oo,gl_PointCoord)*gl_FragColor)-vec4(0.15,0.15,0.15,0.0);
#else
gl_FragColor=(texture2D(oo,gl_PointCoord)*gl_FragColor)+vec4(0.27,0.27,0.27,0.0);
#endif
gl_FragColor.a*=a;}else{gl_FragColor.a*=(0.3*a);}if(gl_FragColor.r<=1e-6&&gl_FragColor.g<=1e-6&&gl_FragColor.b<=1e-6){gl_FragColor.rgb=vec3(0.1,0.1,0.1);gl_FragColor.a=0.1;}}

