#else
d=wgs84ToWebMerc(d);f=wgs84ToWebMerc(f);g=(d+f)*0.5;h=vec3(0.0,0.0,1.0);i=d-f;k=length(i);g=g+h*(k*0.3);if(k<=1000.0){l=48.0;}else if(k<=10000.0){l=42.0;}else if(k<=100000.0){l=32.0;}else if(k<=1000000.0){l=24.0;}else if(k<=2000000.0){l=36.0;}else{l=floor(k*0.000006);}if(drawPath){n=$auxpos1.z;}else{n=mod($auxpos1.z+(l*2.0+1.0)/ls*oi,l*2.0+1.0);}if((n>=l)&&(n<(l*2.0+1.0))){m=-1.0;n-=l;}vec3 o=normalize(i);vec3 p=g+o*(k*0.3*m);float q=n/l;q=clamp(q,0.0,1.0);if(m<=0.0){j=getPoint(q,g,p,f,f);}else{j=getPoint(q,d,d,p,g);}hd=vec3(0.0,0.0,1.0);
#endif
gl_Position=ps*se*vec4(j,1.0);dh=j;a=fract($auxpos1.y/ss.x);b=floor($auxpos1.y/ss.y)/ss.y;float r=(texture2D(om,vec2(a,b))).r;float s=getLinearValue(le,r);if(!drawPath){gl_PointSize=sl*pow(2.718,s);}cc=texture2D(ee,vec2(s,0.5));}else{vec3 j=vec3(0.0,0.0,0.0);gl_Position=ps*se*vec4(j,1.0);dh=j;if(!drawPath) gl_PointSize=sl;cc=vec4(1.0,1.0,0.0,1.0);hd=normalize(j);}}
