attribute vec3 $position;attribute vec3 $auxpos1;uniform mat4 mo;uniform mat4 ls;uniform bool lo;uniform float os;uniform sampler2D so;uniform sampler2D oi;uniform vec2 oo;uniform float ii;uniform vec2 pi;uniform float ol;uniform vec2 op;uniform bool mm;uniform sampler2D ie;varying vec3 gc;varying vec3 dc;varying vec2 hd;varying vec4 gg; $lonlat2position  $translationMat  $linearInterpolator  $localTrans  $expoEaseOut const float a=0.0;const float b=1.0;const float c=2.0;const float d=3.0;void main(void){float e=fract($auxpos1.x/oo.x);float f=floor($auxpos1.x/oo.y)/oo.y;float g=(texture2D(so,vec2(e,f))).r;float h=(texture2D(oi,vec2(e,f))).r;float i=mod(ol,ii);float j=getExpoEaseOutValue(i,g,h,ii);if(mm) j=h;float k=getLinearValue(op,j);if(lo){float l=200000.0;if($auxpos1.y==a||$auxpos1.y==d){l=getScope(pi,k);}else if($auxpos1.y==b||$auxpos1.y==c){l=1.25*getScope(pi,k);}if($auxpos1.y==a) hd=vec2(0.0,0.0);else if($auxpos1.y==d) hd=vec2(0.0,1.0);else if($auxpos1.y==b) hd=vec2(1.0,0.0);else if($auxpos1.y==c) hd=vec2(1.0,1.0);float m=$auxpos1.z*2.0*PI;vec3 n=vec3(l*cos(m),l*sin(m),0.0);vec3 o,p;mat4 q=mat4(1.0);
#ifdef GLOBAL
mat4 r=getTransMat($position);q=toRotationMat(r);o=(r*vec4(n,1.0)).xyz;vec4 s=vec4(0.0,0.0,1.0,0.0);p=(q*s).xyz;
#else
o=wgs84ToWebMerc($position);o+=n;p=vec3(1.0,0.0,1.0);
#endif
gl_Position=mo*ls*vec4(o,1.0);gc=normalize(p);dc=o;gg=texture2D(ie,vec2(k,0.5));}else{float t=getScope(pi,k);mat4 r=getTransMat($position);float u=($auxpos1.y-os/2.0)*(20.0/os);float v=($auxpos1.z-os/2.0)*(20.0/os);float w=exp(-0.0831*(u*u+v*v));float x=atan(u,v);u=w*cos(2.0*x);v=w*sin(2.0*x);float y=sqrt(u*u+v*v)*getScope(pi,k);vec3 o=(r*vec4(t*($auxpos1.y/os-0.5),t*($auxpos1.z/os-0.5),y,1.0)).xyz;vec3 p=normalize(o);gl_Position=mo*ls*vec4(o,1.0);gc=p;dc=o;hd=$auxpos1.yz/(os-1.0);}}
