uniform vec3 os;uniform vec4 es;uniform vec4 il;uniform vec4 ms;uniform vec3 ls;uniform float si;uniform vec3 ss;varying vec2 hg;varying vec3 ch;varying vec3 dh;varying vec4 hc;const vec3 a=vec3(1.0,1.0,1.0);const vec3 b=vec3(1.0,1.0,1.0);const vec3 c=vec3(0.4,0.4,0.4);void main(){vec3 d=normalize(dh-ls);vec3 e=normalize(ch);vec3 f=normalize(reflect(d,e));float g=max(dot(f,os),.001);vec3 h=c*ms.rgb*ms.w*pow(g,16.0);vec3 i=a*il.rgb*il.w*clamp(dot(e,os),.0,1.0);vec3 j=b*es.rgb*es.w;vec3 k=vec3(j+i+h);float l=abs(hg.x-0.5)/0.5;gl_FragColor=(1.0-l)*vec4(ss,1.0)+l*hc;gl_FragColor*=vec4(k,1.0);float m=si*0.01;gl_FragColor.w=m;}

attribute vec3 $position;attribute vec3 $auxpos1;uniform mat4 pi;uniform mat4 ep;uniform bool li;uniform float mp;uniform sampler2D io;uniform sampler2D op;uniform vec2 om;uniform float ps;uniform vec2 ml;uniform float is;uniform vec2 ii;uniform bool oo;uniform sampler2D oe;varying vec3 ch;varying vec3 dh;varying vec2 hg;varying vec4 hc; $lonlat2position  $translationMat  $linearInterpolator  $localTrans  $expoEaseOut const float a=0.0;const float b=1.0;const float c=2.0;const float d=3.0;void main(void){float e=fract($auxpos1.x/om.x);float f=floor($auxpos1.x/om.y)/om.y;float g=(texture2D(io,vec2(e,f))).r;float h=(texture2D(op,vec2(e,f))).r;float i=mod(is,ps);float j=getExpoEaseOutValue(i,g,h,ps);if(oo) j=h;float k=getLinearValue(ii,j);if(li){float l=200000.0;if($auxpos1.y==a||$auxpos1.y==d){l=getScope(ml,k);}else if($auxpos1.y==b||$auxpos1.y==c){l=1.25*getScope(ml,k);}if($auxpos1.y==a) hg=vec2(0.0,0.0);else if($auxpos1.y==d) hg=vec2(0.0,1.0);else if($auxpos1.y==b) hg=vec2(1.0,0.0);else if($auxpos1.y==c) hg=vec2(1.0,1.0);float m=$auxpos1.z*2.0*PI;vec3 n=vec3(l*cos(m),l*sin(m),0.0);vec3 o,p;mat4 q=mat4(1.0);
#ifdef GLOBAL
mat4 r=getTransMat($position);q=toRotationMat(r);o=(r*vec4(n,1.0)).xyz;vec4 s=vec4(0.0,0.0,1.0,0.0);p=(q*s).xyz;
#else
o=wgs84ToWebMerc($position);o+=n;p=vec3(1.0,0.0,1.0);
#endif
gl_Position=pi*ep*vec4(o,1.0);ch=normalize(p);dh=o;hc=texture2D(oe,vec2(k,0.5));}else{float t=getScope(ml,k);mat4 r=getTransMat($position);float u=($auxpos1.y-mp/2.0)*(20.0/mp);float v=($auxpos1.z-mp/2.0)*(20.0/mp);float w=exp(-0.0831*(u*u+v*v));float x=atan(u,v);u=w*cos(2.0*x);v=w*sin(2.0*x);float y=sqrt(u*u+v*v)*getScope(ml,k);vec3 o=(r*vec4(t*($auxpos1.y/mp-0.5),t*($auxpos1.z/mp-0.5),y,1.0)).xyz;vec3 p=normalize(o);gl_Position=pi*ep*vec4(o,1.0);ch=p;dh=o;hg=$auxpos1.yz/(mp-1.0);}}
