uniform vec3 pp;uniform vec4 mp;uniform vec4 ps;uniform vec4 ip;uniform vec3 ml;uniform float ei;uniform vec3 sp;varying vec2 hh;varying vec3 cg;varying vec3 gg;varying vec4 hg;const vec3 a=vec3(1.0,1.0,1.0);const vec3 b=vec3(1.0,1.0,1.0);const vec3 c=vec3(0.4,0.4,0.4);void main(){vec3 d=normalize(gg-ml);vec3 e=normalize(cg);vec3 f=normalize(reflect(d,e));float g=max(dot(f,pp),.001);vec3 h=c*ip.rgb*ip.w*pow(g,16.0);vec3 i=a*ps.rgb*ps.w*clamp(dot(e,pp),.0,1.0);vec3 j=b*mp.rgb*mp.w;vec3 k=vec3(j+i+h);float l=abs(hh.x-0.5)/0.5;gl_FragColor=(1.0-l)*vec4(sp,1.0)+l*hg;gl_FragColor*=vec4(k,1.0);float m=ei*0.01;gl_FragColor.w=m;}

attribute vec3 $position;attribute vec3 $auxpos1;uniform mat4 ol;uniform mat4 es;uniform bool om;uniform float ms;uniform sampler2D ee;uniform sampler2D pm;uniform vec2 lm;uniform float mm;uniform vec2 lo;uniform float op;uniform vec2 ls;uniform bool os;uniform sampler2D ss;varying vec3 cg;varying vec3 gg;varying vec2 hh;varying vec4 hg; $lonlat2position  $translationMat  $linearInterpolator  $localTrans  $expoEaseOut const float a=0.0;const float b=1.0;const float c=2.0;const float d=3.0;void main(void){float e=fract($auxpos1.x/lm.x);float f=floor($auxpos1.x/lm.y)/lm.y;float g=(texture2D(ee,vec2(e,f))).r;float h=(texture2D(pm,vec2(e,f))).r;float i=mod(op,mm);float j=getExpoEaseOutValue(i,g,h,mm);if(os) j=h;float k=getLinearValue(ls,j);if(om){float l=200000.0;if($auxpos1.y==a||$auxpos1.y==d){l=getScope(lo,k);}else if($auxpos1.y==b||$auxpos1.y==c){l=1.25*getScope(lo,k);}if($auxpos1.y==a) hh=vec2(0.0,0.0);else if($auxpos1.y==d) hh=vec2(0.0,1.0);else if($auxpos1.y==b) hh=vec2(1.0,0.0);else if($auxpos1.y==c) hh=vec2(1.0,1.0);float m=$auxpos1.z*2.0*PI;vec3 n=vec3(l*cos(m),l*sin(m),0.0);vec3 o,p;mat4 q=mat4(1.0);
#ifdef GLOBAL
mat4 r=getTransMat($position);q=toRotationMat(r);o=(r*vec4(n,1.0)).xyz;vec4 s=vec4(0.0,0.0,1.0,0.0);p=(q*s).xyz;
#else
o=wgs84ToWebMerc($position);o+=n;p=vec3(1.0,0.0,1.0);
#endif
gl_Position=ol*es*vec4(o,1.0);cg=normalize(p);gg=o;hg=texture2D(ss,vec2(k,0.5));}else{float t=getScope(lo,k);mat4 r=getTransMat($position);float u=($auxpos1.y-ms/2.0)*(20.0/ms);float v=($auxpos1.z-ms/2.0)*(20.0/ms);float w=exp(-0.0831*(u*u+v*v));float x=atan(u,v);u=w*cos(2.0*x);v=w*sin(2.0*x);float y=sqrt(u*u+v*v)*getScope(lo,k);vec3 o=(r*vec4(t*($auxpos1.y/ms-0.5),t*($auxpos1.z/ms-0.5),y,1.0)).xyz;vec3 p=normalize(o);gl_Position=ol*es*vec4(o,1.0);cg=p;gg=o;hh=$auxpos1.yz/(ms-1.0);}}
